const VerkaufsstelleBearbeiten = ({station}) => {
    return ( 
        <div className="main">
            <p className="home-headline">Verkaufstelle bearbeiten</p>
            <Form>
           <Form.Field>
               <Form.Input
                fluid
                label='Standort'
                placeholder="Standort"
                value={location}
                onChange={e => setLocation(e.target.value)}
                />
           </Form.Field>
           
           <Form.Group widths='equal'>
                <Form.Input
                    fluid
                    label='Longitude'
                    placeholder="Longitude"
                    value={longitude}
                    onChange={e => setLongitude(e.target.value)}
                />
                
                <Form.Input
                    fluid
                    label='Latitude'
                    placeholder="Latitude"
                    value={latitude}
                    onChange={e => setLatitude(e.target.value)}
                />
                
               <Form.Input
                    fluid
                    label='Typ'
                    placeholder='Typ'
                    value={type}
                    onChange={e => setType(e.target.value)}
               />
            </Form.Group>
        
               <Form.TextArea
                fluid
                label='Beschreibung'
                placeholder="Beschreibung"
                value={description}
                onChange={e => setDescription(e.target.value)}
                />

            <Link to='/verkaufsstellen-auflisten'>
                <Form.Field>
                    <Button
                    onClick={async () => {
                        const station = {location, longitude, latitude, type, description};
                        const response = await fetch("/add_station", {
                            method: "POST",
                            headers: {
                                "Content-Type": "application/json"
                            },
                            body: JSON.stringify(station)
                        });

                        if (response.ok){
                            console.log("response worked!");
                            onNewStation(station);
                            setLocation("");
                            setLongitude("");
                            setType("");
                            setLatitude("");
                            setDescription("");
                        }
                    }}
                    >Submit</Button>
                </Form.Field>
            </Link>
        </Form> 
        </div>
     );
}
 
export default VerkaufsstelleBearbeiten;
