import { useEffect, useState } from 'react';
import {Bar} from 'react-chartjs-2';

const TopStationsDoughnut = () => {

	const [sellers, setSellers] = useState([]);

	useEffect(() => {
		fetch("/sellers/top_sellers").then(response =>
			response.json().then(data => {
				setSellers(data.sellers)

			}));
	}, []);

	

	const [chartData, setChartData] = useState({});

	const chart = (sellers) => {
		setChartData ({
			labels: [ sellers[0].name, sellers[1].name, sellers[2].name, sellers[3].name, sellers[4].name],
			datasets: [
				{ 
					label: 'Top 5 sellers',
					data: [ sellers[0].amount, sellers[1].amount, sellers[2].amount, sellers[3].amount, sellers[4].amount ],
					backgroundColor: [
						'rgb(255, 99, 132)',
						'rgb(54, 162, 235)',
						'rgb(255, 205, 86)'
					],
					hoverOffset: 4
				}
			]
		})
	}

	useEffect(() => {
		chart(sellers)
	}, [])

	return ( 
		<div>
			<Bar data={chartData}/>
		</div>
	 );
}
 
export default TopStationsDoughnut;
