import { useEffect, useState } from 'react';
import {Bar} from 'react-chartjs-2';

const TopStationsDoughnut = () => {

	const [chartData, setChartData] = useState({});
	const [sellerName, setSellerName] = useState([]);
	const [sellerAmount, setSellerAmount] = useState([]);


	const chart = (sellers) => {	
		
		let selName = [];
		let selAmount = []; 

		fetch("/sellers/top_sellers").then(response => {
			for (const dataObj of response.data.data){
				selName.push(dataObj.name);
				selAmount.push(dataObj.amount);
			}
		}
			
		);
			
		setChartData ({
			labels: selName ,
			datasets: [
				{ 
					label: 'Top 5 sellers',
					data: selAmount,
					backgroundColor: [
						'rgb(255, 99, 132)',
						'rgb(54, 162, 235)',
						'rgb(255, 205, 86)'
					],
					hoverOffset: 4
				}
			]
			})
	}

	useEffect(() => {
		chart(sellers)
	}, [])

	return ( 
		<div>
			<Bar data={chartData}/>
		</div>
	 );
}
 
export default TopStationsDoughnut;
