import {useEffect, useState} from 'react';
import Stations from './Stations';
import {Table} from 'semantic-ui-react';

const VerkaufsstellenAuflisten = () => {
    const [stations, setStations]= useState([]);
    
    useEffect(() => {
        fetch("/stations").then(response =>
          response.json().then(data => {
            setStations(data.stations);
          })
        );
      }, []);

    function deleteStation(stationID){
        alert(stationID)
        /*
        fetch('/stations/'+stationID,{
            method: 'DELETE',
            header:{'Content-Type': 'application/json',
                    'Accept': 'application/json'
                    }
        })
         */
    };

    return (
        <div className="main">
          <p className="home-headline">Verkaustellen auflisten</p>
          <Table color='black'>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>Standort</Table.HeaderCell>
                <Table.HeaderCell>Longitude</Table.HeaderCell>
                <Table.HeaderCell>Latitude</Table.HeaderCell>
                <Table.HeaderCell>Typ</Table.HeaderCell>
                <Table.HeaderCell>Beschreibung</Table.HeaderCell>
              </Table.Row>
            </Table.Header>
            <Table.Body>
              {stations.map((station) => (
              <Stations key={station.id} station={station} deleteStation={deleteStation} x={station.location}/>
              ))}
            </Table.Body>
          </Table>
        </div>
      );
}
 
export default VerkaufsstellenAuflisten;
