import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const VerkaufsstelleEroeffnen = () => {
    
    var map = L.map('mapid').setView([47.721053, 10.307946], 13);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
    }).addTo(map);

    var marker = L.marker([47.721053, 10.307946])//.addTo(map)



    function onMapClick(e){
        marker.setLatLng(e.latlng)
        marker.addTo(map)
        document.getElementById("lat").value = e.latlng.lat
        document.getElementById("lon").value = e.latlng.lng
        document.getElementById("gewaehlter").innerHTML = e.latlng.lat+"|"+e.latlng.lng
        console.log(e.latlng)
    }

    map.on('click', onMapClick)

    return (
        
        <div className="main">
            <p className="home-headline">Verkaufsstelle er√∂ffnen</p>
                <div id="mapid"></div>
        </div>
        
    );
}
 
export default VerkaufsstelleEroeffnen;
